$(document).ready(function(){
   var ball = $('#ball');
   var speed = 500; //ball speed 
   var distance = $(window).height() - ball.height(); //calculate the distance to the top from the ball

   function bounceBall() {
     ball.animate({ top: distance },speed, "swing")
     .animate({ top: 0},speed,"swing",bounceBall);

   }
   bounceBall(); //start bounce
});